const display=(i,s)=>{s.innerHTML=i.map((i=>{const{id:s,data:e,img:l,title:a,alt:n,description:t}=i;return`<li class="our-blog__item" data-aos="zoom-in-up">\n    <div class="our-blog__image">\n      <picture>\n        <source type="image/webp" srcset="${l}.webp">\n        <img src="${l}.jpg"" alt="${n}">\n      </picture>\n    </div>\n    <p class="blog-publish__date">${e}</p>\n    <h6 class="blog-publish__title">${a}</h6>\n    <p class="blog-publish__text">${t}</p>\n    <div class="blog-view__wrapper">\n      <a class="view-link view-link__more" href="read-blog.html?id=${s}">Read More\n        <svg width="25" height="12" class="arrow-right">\n          <use xlink:href="#arrow-right-icon"></use>\n        </svg>\n      </a>\n    </div>\n  </li>`})).join("")};export default display;